# Reads a shell script from \"user_script.sh\" and executes it securely using /bin/bash
def runScript():
    with open("user_script.sh", "r") as f:
        script = f.read()
    subprocess.run(
        script,
        shell=True,
        executable="/bin/bash",
        check=True,
        env={"PATH": "/usr/bin", "SAFE_MODE": "1"}
)
